@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;

  /* AI Nebula Theme Colors */
  --primary-bg: hsl(240, 75%, 8%);
  --secondary-bg: hsl(243, 45%, 9%);
  --glass-bg: hsla(243, 45%, 9%, 0.85);
  --primary-text: hsl(245, 100%, 94%);
  --border-color: hsla(270, 50%, 65%, 0.3);
  --glow-color: hsla(270, 75%, 85%, 0.8);
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    @apply font-sans antialiased;
    background: linear-gradient(135deg, var(--primary-bg) 0%, var(--secondary-bg) 100%);
    color: var(--primary-text);
    font-family: 'Inter', 'Noto Sans SC', sans-serif;
    overflow: hidden;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  html, body, #root {
    height: 100%;
    width: 100%;
  }
}

/* Loading Screen Styles */
.loading-screen {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: var(--primary-bg);
  z-index: 1000;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  transition: opacity 0.8s ease, visibility 0.8s ease;
}

.loading-screen.hidden {
  opacity: 0;
  visibility: hidden;
}

.loading-spinner {
  width: 60px;
  height: 60px;
  border: 3px solid hsla(270, 50%, 65%, 0.2);
  border-top: 3px solid var(--glow-color);
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-bottom: 20px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes glow {
  0% { filter: brightness(1) saturate(1); }
  100% { filter: brightness(1.2) saturate(1.3); }
}

.loading-text {
  font-size: 1.2rem;
  color: var(--primary-text);
  margin-bottom: 10px;
}

.loading-progress {
  width: 200px;
  height: 4px;
  background: hsla(270, 50%, 65%, 0.2);
  border-radius: 2px;
  overflow: hidden;
}

.loading-progress-bar {
  height: 100%;
  background: linear-gradient(90deg, var(--glow-color), hsl(207, 90%, 54%));
  border-radius: 2px;
  transition: width 0.3s ease;
  animation: glow 2s ease-in-out infinite alternate;
}

/* 3D Canvas Container */
.ai-nebula-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
  cursor: grab;
  touch-action: none;
}

.ai-nebula-container:active {
  cursor: grabbing;
}

.ai-nebula-container canvas {
  display: block;
  outline: none;
}

/* UI Overlay System */
.ui-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 10;
  pointer-events: none;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  padding: clamp(1rem, 4vw, 2rem);
}

/* Header Section */
.header-section {
  text-align: center;
  pointer-events: none;
}

.main-title {
  font-size: clamp(2rem, 5vw, 3.5rem);
  font-weight: 700;
  color: hsl(0, 0%, 100%);
  text-shadow: 
    0 0 15px var(--glow-color),
    0 0 25px hsla(270, 75%, 85%, 0.5),
    0 0 35px hsla(270, 75%, 85%, 0.3);
  margin-bottom: 0.5rem;
  animation: float 6s ease-in-out infinite;
}

.subtitle {
  font-size: clamp(0.9rem, 2vw, 1.1rem);
  color: hsl(220, 13%, 82%);
  font-weight: 300;
  opacity: 0.9;
}

/* Navigation System */
.navigation-container {
  pointer-events: all;
  position: relative;
}

.nav-buttons {
  display: flex;
  gap: 0.75rem;
  background: hsla(0, 0%, 0%, 0.25);
  padding: 0.75rem 1.25rem;
  border-radius: 50px;
  backdrop-filter: blur(15px);
  border: 1px solid hsla(0, 0%, 100%, 0.1);
  box-shadow: 
    0 8px 32px hsla(0, 0%, 0%, 0.3),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.1);
  flex-wrap: wrap;
  justify-content: center;
}

.nav-button {
  background: transparent;
  border: 1px solid transparent;
  color: var(--primary-text);
  padding: 0.6rem 1.2rem;
  border-radius: 25px;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  font-family: inherit;
  font-size: 0.9rem;
  font-weight: 500;
  position: relative;
  overflow: hidden;
  white-space: nowrap;
}

.nav-button:hover {
  background: hsla(0, 0%, 100%, 0.15);
  border-color: hsla(0, 0%, 100%, 0.3);
  transform: translateY(-2px);
  box-shadow: 0 10px 25px hsla(0, 0%, 0%, 0.4);
  color: hsl(0, 0%, 100%);
}

.nav-button.active {
  background: hsla(0, 0%, 100%, 0.1);
  border-color: hsla(0, 0%, 100%, 0.4);
  color: hsl(0, 0%, 100%);
  box-shadow: 
    0 4px 15px hsla(0, 0%, 0%, 0.3),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.2);
}

.nav-button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, hsla(0, 0%, 100%, 0.3), transparent);
  transform: skewX(-30deg);
  transition: left 0.6s ease;
}

.nav-button:hover::before {
  left: 100%;
}

/* Instructions */
.instruction-text {
  position: absolute;
  bottom: 8%;
  color: hsla(0, 0%, 100%, 0.6);
  font-size: clamp(0.8rem, 2vw, 0.95rem);
  text-align: center;
  transition: opacity 1.5s ease-in-out;
  pointer-events: none;
  text-shadow: 0 2px 8px hsla(0, 0%, 0%, 0.5);
}

.instruction-text.fade-out {
  opacity: 0;
}

/* Tool Detail Panel */
.tool-detail-panel {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) scale(0.9);
  width: 90%;
  max-width: 550px;
  max-height: 80vh;
  background: hsla(243, 45%, 9%, 0.95);
  backdrop-filter: blur(25px);
  border: 1px solid hsla(270, 50%, 65%, 0.4);
  border-radius: 20px;
  padding: 2rem;
  z-index: 100;
  opacity: 0;
  pointer-events: none;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 
    0 25px 50px hsla(0, 0%, 0%, 0.5),
    0 0 50px hsla(270, 50%, 65%, 0.2),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.1);
  overflow-y: auto;
}

.tool-detail-panel.visible {
  opacity: 1;
  transform: translate(-50%, -50%) scale(1);
  pointer-events: all;
}

.close-panel-btn {
  position: absolute;
  top: 1rem;
  right: 1rem;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: hsla(0, 0%, 100%, 0.1);
  border: 1px solid hsla(0, 0%, 100%, 0.2);
  color: var(--primary-text);
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
}

.close-panel-btn:hover {
  background: hsla(0, 0%, 100%, 0.2);
  color: hsl(0, 0%, 100%);
  transform: rotate(90deg);
}

.detail-header {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid hsla(0, 0%, 100%, 0.1);
}

.detail-icon {
  font-size: 3rem;
  filter: drop-shadow(0 0 10px var(--glow-color));
}

.detail-info h3 {
  font-size: 1.8rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  color: hsl(0, 0%, 100%);
}

.detail-category {
  display: inline-block;
  background: hsla(207, 90%, 54%, 0.2);
  color: hsl(207, 90%, 54%);
  padding: 0.3rem 0.8rem;
  border-radius: 12px;
  font-size: 0.8rem;
  font-weight: 500;
  border: 1px solid hsla(207, 90%, 54%, 0.3);
}

.detail-description {
  font-size: 1rem;
  line-height: 1.6;
  margin-bottom: 1.5rem;
  color: hsl(220, 13%, 82%);
}

.detail-stats {
  display: grid;
  grid-template-columns: auto 1fr;
  gap: 1rem;
  align-items: center;
  margin-bottom: 1.5rem;
  padding: 1rem;
  background: hsla(0, 0%, 100%, 0.05);
  border-radius: 12px;
  border: 1px solid hsla(0, 0%, 100%, 0.1);
}

.proficiency-ring {
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.1rem;
  font-weight: 700;
  color: hsl(0, 0%, 100%);
  position: relative;
  background: conic-gradient(from 0deg, hsl(207, 90%, 54%), hsl(270, 75%, 70%), hsl(330, 75%, 70%), hsl(207, 90%, 54%));
  padding: 3px;
}

.proficiency-ring::before {
  content: '';
  position: absolute;
  inset: 3px;
  background: var(--secondary-bg);
  border-radius: 50%;
  z-index: 1;
}

.proficiency-ring span {
  position: relative;
  z-index: 2;
}

.proficiency-info {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.proficiency-label {
  font-size: 0.9rem;
  color: hsl(240, 4%, 64%);
  font-weight: 500;
}

.proficiency-description {
  font-size: 0.85rem;
  color: hsl(220, 13%, 82%);
  line-height: 1.4;
}

.detail-features {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
}

.feature-tag {
  padding: 0.4rem 0.8rem;
  background: hsla(270, 50%, 65%, 0.2);
  color: hsl(270, 75%, 85%);
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 500;
  border: 1px solid hsla(270, 50%, 65%, 0.3);
  transition: all 0.3s ease;
}

.feature-tag:hover {
  background: hsla(270, 50%, 65%, 0.3);
  transform: translateY(-1px);
}

/* Performance Indicators */
.fps-monitor {
  position: fixed;
  top: 1rem;
  right: 1rem;
  background: hsla(0, 0%, 0%, 0.7);
  color: hsl(120, 100%, 50%);
  padding: 0.5rem;
  border-radius: 8px;
  font-family: monospace;
  font-size: 0.8rem;
  z-index: 50;
  display: none;
}

/* Responsive Design */
@media (max-width: 768px) {
  .nav-buttons {
    flex-direction: column;
    gap: 0.5rem;
    padding: 1rem;
  }

  .nav-button {
    width: 100%;
    text-align: center;
  }

  .tool-detail-panel {
    width: 95%;
    padding: 1.5rem;
    max-height: 85vh;
  }

  .detail-header {
    flex-direction: column;
    text-align: center;
    gap: 0.5rem;
  }

  .detail-stats {
    grid-template-columns: 1fr;
    text-align: center;
  }
}

@media (max-width: 480px) {
  .ui-overlay {
    padding: 1rem;
  }

  .tool-detail-panel {
    padding: 1rem;
  }

  .detail-header .detail-icon {
    font-size: 2.5rem;
  }
}

/* Accessibility */
@media (prefers-reduced-motion: reduce) {
  .loading-spinner,
  .main-title,
  .nav-button::before,
  .loading-progress-bar {
    animation: none;
  }

  .nav-button:hover,
  .close-panel-btn:hover,
  .feature-tag:hover {
    transform: none;
  }
}

/* High contrast mode */
@media (prefers-contrast: high) {
  :root {
    --border-color: hsla(0, 0%, 100%, 0.8);
    --primary-text: hsl(0, 0%, 100%);
  }

  .nav-button {
    border-color: hsla(0, 0%, 100%, 0.5);
  }

  .tool-detail-panel {
    border-color: hsla(0, 0%, 100%, 0.8);
  }
}

